// StillMe Desktop - Main App Component
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { CssBaseline, Box, Snackbar, Alert } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store/store';
import { useAppDispatch, useAppSelector } from './store/hooks';
import { setConnectionStatus, addNotification } from './store/slices/appSlice';

// Components
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Settings from './components/Settings';
import FileManager from './components/FileManager';
import SystemTray from './components/SystemTray';
import LoadingScreen from './components/LoadingScreen';

// Services
import { WebSocketService } from './services/websocket';
import { NotificationService } from './services/notifications';
import { FileService } from './services/files';

// Theme
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
  components: {
    MuiAppBar: {
      styleOverrides: {
        root: {
          backgroundColor: '#1e1e1e',
        },
      },
    },
    MuiDrawer: {
      styleOverrides: {
        paper: {
          backgroundColor: '#1e1e1e',
          borderRight: '1px solid #333',
        },
      },
    },
  },
});

const lightTheme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function AppContent() {
  const dispatch = useAppDispatch();
  const { theme, connectionStatus, notifications } = useAppSelector((state) => state.app);
  const [isLoading, setIsLoading] = useState(true);
  const [wsService, setWsService] = useState<WebSocketService | null>(null);

  useEffect(() => {
    initializeApp();
  }, []);

  const initializeApp = async () => {
    try {
      // Initialize services
      const ws = new WebSocketService();
      await ws.connect();
      setWsService(ws);

      // Initialize notification service
      const notificationService = new NotificationService();
      await notificationService.initialize();

      // Initialize file service
      const fileService = new FileService();
      await fileService.initialize();

      // Set up WebSocket event handlers
      ws.on('connected', () => {
        dispatch(setConnectionStatus('connected'));
        dispatch(addNotification({
          id: Date.now().toString(),
          type: 'success',
          message: 'Connected to StillMe Gateway',
          timestamp: Date.now()
        }));
      });

      ws.on('disconnected', () => {
        dispatch(setConnectionStatus('disconnected'));
        dispatch(addNotification({
          id: Date.now().toString(),
          type: 'error',
          message: 'Disconnected from StillMe Gateway',
          timestamp: Date.now()
        }));
      });

      ws.on('message', (message) => {
        handleIncomingMessage(message);
      });

      ws.on('error', (error) => {
        dispatch(addNotification({
          id: Date.now().toString(),
          type: 'error',
          message: `WebSocket Error: ${error.message}`,
          timestamp: Date.now()
        }));
      });

      setIsLoading(false);
    } catch (error) {
      console.error('Failed to initialize app:', error);
      dispatch(addNotification({
        id: Date.now().toString(),
        type: 'error',
        message: 'Failed to initialize application',
        timestamp: Date.now()
      }));
      setIsLoading(false);
    }
  };

  const handleIncomingMessage = (message: any) => {
    switch (message.type) {
      case 'notification':
        dispatch(addNotification({
          id: message.id || Date.now().toString(),
          type: 'info',
          message: message.data?.body || message.message,
          timestamp: message.timestamp || Date.now()
        }));
        break;
      case 'status_update':
        // Handle status updates
        break;
      case 'command_response':
        // Handle command responses
        break;
      default:
        console.log('Unknown message type:', message.type);
    }
  };

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <ThemeProvider theme={theme === 'dark' ? darkTheme : lightTheme}>
      <CssBaseline />
      <Box sx={{ display: 'flex', height: '100vh' }}>
        <Sidebar />
        <Box component="main" sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
          <Routes>
            <Route path="/" element={<Navigate to="/chat" replace />} />
            <Route path="/chat" element={<ChatInterface wsService={wsService} />} />
            <Route path="/files" element={<FileManager />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </Box>
        <SystemTray />
      </Box>
      
      {/* Notifications */}
      {notifications.map((notification) => (
        <Snackbar
          key={notification.id}
          open={true}
          autoHideDuration={6000}
          onClose={() => {
            // Remove notification
          }}
          anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
        >
          <Alert
            onClose={() => {
              // Remove notification
            }}
            severity={notification.type as any}
            sx={{ width: '100%' }}
          >
            {notification.message}
          </Alert>
        </Snackbar>
      ))}
    </ThemeProvider>
  );
}

function App() {
  return (
    <Provider store={store}>
      <Router>
        <AppContent />
      </Router>
    </Provider>
  );
}

export default App;
